import {useRef, useEffect, useState} from 'react';
import styled from 'styled-components';

const SliderBackground = styled.div`
    width: 100%;
    height: 50%;
    background-color: #000;
    border-radius: 10px;
    position: relative;
    `;

const SliderThumb = styled.div`
    width: 20px;
    height: 20px;
    background-color: #fff;
    border-radius: 50%;
    position: absolute;
    top: 50%;
    left: 0;
    transform: translate(-50%, -50%);
    `;

export default function Slider({
    min,
    max,
    steps,
    color,
    name,
}) {
    const [sliderValue, setSliderValue] = useState(min);
    const slider = useRef(null);

    useEffect(() => {
        const sliderElement = slider.current;
        const sliderThumb = sliderElement.querySelector(`#slider-thumb-${name}`);
        const sliderBackground = sliderElement.querySelector(`#slider-background-${name}`);
    }, []);
    return(
        <div ref={slider} id={`slider-${name}`} style={{position: 'relative', width: '100%', height: '20px', display: 'flex', flexDirection: 'column', justifyContent: 'center'}}>
            <SliderBackground/>
            <div id={`slider-thumb-${name}`}></div>
        </div>
    )
}
