import {useEffect, useState} from 'react';
import styled from 'styled-components';

const SliderBackground = styled.div`
    width: 100%;
    height: 50%;
    background-color: #000;
    border-radius: 10px;
    position: relative;
    `;

export default function Slider({
    min=0,
    max=100,
    steps,
    color,
    name='',
}) {
    const [sliderValue, setSliderValue] = useState(min);

    useEffect(() => {
        const slider = document.querySelector(`#slider-${name}`);
        const sliderThumb = document.querySelector(`#slider-thumb-${name}`);


        // event listener that tracks mouse positionX when sliderThumb is clicked and dragged across slider
        sliderThumb.addEventListener('mousedown', (e) => {
            window.addEventListener('mouseup', (e) => {
                const sliderWidth = slider.offsetWidth;
                const stepDistance = sliderWidth / steps;
                console.log('test up');
            });
            const sliderWidth = slider.offsetWidth;
            const stepDistance = sliderWidth / steps;
            console.log('test down');
        });



    }, []);
    return(
        <div id={`slider-${name}`} style={{position: 'relative', width: '80%', height: '20px', margin: '0 auto', display: 'flex', flexDirection: 'column', justifyContent: 'center'}}>
            <SliderBackground/>
            <div id={`slider-controller${name}`} style={{position: 'absolute', width: '100%', height: '100%'}}></div>
            <div id={`slider-thumb-${name}`} style={{width: '20px', height: '20px', backgroundColor: 'green', borderRadius: '50%', top: '50%', position: 'absolute', left: '0', transform: 'translate(-50%, -50%)'}}></div>
        </div>
    )
}
