import {createSlice} from '@reduxjs/toolkit';

const homeSlice = createSlice({
    name: 'home',
    initialState: {
        settingsOpen: true,
        settingsDevice: 'desktop',
        prompt: '',
        meshID: 4,
        modelDisplay: true,
        density: 1024,
        resolution: 32,
    },
    reducers: {
            UPDATE_PROMPT: (state, action) => {
                state.prompt = action.payload;
            },
            SETTINGS_TOGGLE: (state) => {
                state.settingsOpen = !state.settingsOpen;
            },
            SET_MESHID: (state, action) => {
                state.meshID = action.payload;
            },
            MODEL_DISPLAY_TOGGLE: (state) => {
                state.modelDisplay = !state.modelDisplay;
            },
            UPDATE_DENSITY: (state, action) => {
                state.density = action.payload;
            },
            UPDATE_RESOLUTION: (state, action) => {
                state.resolution = action.payload;
            },
            TOGGLE_SETTINGS_WIDTH: (state, action) => {
                state.settingsDevice = action.payload;
            }
    }
});

export const {SETTINGS_TOGGLE, UPDATE_PROMPT, SET_MESHID, MODEL_DISPLAY_TOGGLE, UPDATE_DENSITY, UPDATE_RESOLUTION, TOGGLE_SETTINGS_WIDTH} = homeSlice.actions;
export default homeSlice.reducer;
